import React, { useState } from 'react';
import './Dashboard.css';
import Chart from "react-apexcharts";

function Dashboard() {
  const [activities, setActivities] = useState({
    options: {
      chart: {
        id: "basic-bar",
      },
      xaxis: {
        categories: ["week1","week2","week3","week4"]
      },
      plotOptions: {
        bar: { 
          columnWidth: '50%'
        }
      },
      toolbar: null,
      fill: {
        opacity: 1, // Set opacity to 1 for bars
      }

    },
    
    series: [
      {
        name: "User",
        data: [500, 200, 300, 400],
        color: '#98D89E',
      },
      {
        name: "Guest",
        data: [400, 150, 250, 350],
        color: '#EE8484',
      }
    ]
  });
  
  return (
    <div className="dashboard flex  flex-col sm:flex-row h-screen overflow-hidden">
    <div className="w-full sm:w-1/4   flex flex-col justify-between ">
    </div>
    <div className='w-full sm:w-3/4  '>
      <div className='' style={{width:"100%"}}>
      <Chart
              options={activities.options}
              series={activities.series}
              type="bar"
              width="100%"
            />
            </div>
    </div>
    </div>
  )
}

export default Dashboard
